    /* USER TABLE CONTAINING ACCOUNT, DEMOGRAPHIC, AND PROFILE DATA */
    CREATE TABLE user ( 
        user_id INT AUTO_INCREMENT PRIMARY KEY,
        email VARCHAR(150) NOT NULL CHECK(
            email LIKE '%@%' -- contains '@' symbol
            AND email NOT LIKE '%@%@%' -- contains only one '@' symbol
            AND email NOT LIKE '%[ ]%' -- contains no spaces
            AND SUBSTRING_INDEX(email, '@', -1) LIKE '%.%' -- contains a domain with at least one '.'
            AND LENGTH(email) BETWEEN 5 AND 150 -- between 5 to 150 characters
        ),
        first_name VARCHAR(50) NOT NULL, 
        last_name VARCHAR(50) NOT NULL,
        image_url VARCHAR(255) UNIQUE CHECK(image_url LIKE 'https://invitro.ca/api/user/%'), -- unique filepath for user profile image location, optional field
        description VARCHAR(255), -- optional field
        province ENUM('BC', 'AB', 'SK','MB','ON','QC','NB','NS','NL','PE','YK','NU','NW'), -- column must contain null or one of these selection values, optional field
        sex ENUM('Male', 'Female'), -- column must contain null or one of these selection values, optional field
        dob DATE NOT NULL CHECK( -- optional field
            DATEDIFF(getdate(), date_of_birth) >= 6570 -- at least 18 years of age
            AND DATEDIFF(getdate(), date_of_birth) <= 54750 -- no more than 150 years of age
        ), 
        creation_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= creation_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0 -- deleted flag defaults to 0
    );


    /* CONNECTION TABLE CONTAINING CONNECTION REQUESTS BETWEEN USERS */
    CREATE TABLE connection (
        connection_id INT AUTO_INCREMENT PRIMARY KEY,
        sent_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        connection_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(connection_date >= sent_date), -- last modified date must be greater than or equal to creation date
        sender_id INT NOT NULL,
        receiver_id INT NOT NULL, 
        deleted BOOLEAN NOT NULL DEFAULT 0,  -- deleted flag defaults to 0
        FOREIGN KEY(sender_id) REFERENCES user(user_id),
        FOREIGN KEY(receiver_id) REFERENCES user(user_id)
    );


    /* MESSAGE TABLE CONTAINING  DIRECT MESSAGES AND THEIR RESPECTIVE DATA BETWEEN USERS */
    CREATE TABLE message (
        message_id INT AUTO_INCREMENT PRIMARY KEY,
        message TEXT,
        multimedia_url VARCHAR(255) UNIQUE CHECK(multimedia_url LIKE 'https://invitro.ca/api/multimedia/message%'), -- unique filepath for multimedia location
        seen BOOLEAN NOT NULL DEFAULT 0, -- message seen by receiver defaults to 0
        sender_id INT NOT NULL,
        receiver_id INT NOT NULL,
        sent_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= sent_date), -- last modified date must be greater than or equal to creation date
        sender_deleted BOOLEAN NOT NULL DEFAULT 0,  -- deleted flag defaults to 0
        receiver_deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY(sender_id) REFERENCES user(user_id),
        FOREIGN KEY(receiver_id) REFERENCES user(user_id),
        CHECK(multimedia_url is not null or message is not null) -- checks that at least a message or multimedia file is stored
    );


    /* POST TABLE CONTAINING ALL POSTS AND THEIR RESPECTIVE DATA */
    CREATE TABLE post (
        post_id INT AUTO_INCREMENT PRIMARY KEY,
        user_id INT NOT NULL,
        post TEXT,
        multimedia_url VARCHAR(255) UNIQUE CHECK(multimedia_url LIKE 'https://invitro.ca/api/multimedia/post%'), -- unique filepath for multimedia location
        location POINT, -- no constraint posed on coordinates as dependendent on acceptance of privacy permissions in social media platform
        post_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= post_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY (user_id) REFERENCES user(user_id),
        CHECK(post is not null or multimedia_url is not null) -- checks that at least a post or multimedia file is stored
    );


    /* COMMENT TABLE CONTAINING ALL COMMENTS ON POSTS AND OTHER COMMENTS AS WELL AS THEIR RESPECTIVE DATA */
    CREATE TABLE comment (
        comment_id INT AUTO_INCREMENT PRIMARY KEY,
        user_id INT NOT NULL,
        post_id INT NOT NULL,
        parent_comment INT,  -- nullable for comments on posts
        multimedia_url VARCHAR(255) UNIQUE CHECK(multimedia_url LIKE 'https://invitro.ca/api/multimedia/comment%'), -- unique filepath for multimedia location
        comment TEXT,
        seen BOOLEAN NOT NULL DEFAULT 0,  -- comment seen by poster defaults to 0
        location POINT, -- no constraint posed on coordinates as dependendent on acceptance of privacy permissions in social media platform
        comment_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= comment_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        CHECK(multimedia_url is not null or post is not null), -- checks that at least a comment or multimedia file is entered
        FOREIGN KEY(parent_comment) REFERENCES comment(comment_id), -- references existing comment id inserted in table
        FOREIGN KEY(user_id) REFERENCES user(user_id),
        FOREIGN KEY(post_id) REFERENCES post(post_id),
        CHECK(multimedia_url is not null or comment is not null) -- checks that at least a comment or multimedia file is stored    
    );


    /* REACTION TABLE CONTAINING ALL POST AND COMMENT REACTIONS AND THEIR RESPECTIVE DATA */
    CREATE TABLE reaction (
        reaction_id INT PRIMARY KEY AUTO_INCREMENT,
        user_id INT NOT NULL,
        post_id INT, -- nullable if reaction is for a comment
        comment_id INT, -- nullable if reaction is for a post
        reaction ENUM('like', 'heart', 'wow') NOT NULL, -- column must contain one of these selection values
        image_url VARCHAR(255) CHECK(multimedia_url LIKE 'https://invitro.ca/api/reaction/%'), -- filepath for reaction image location
        reaction_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= reaction_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY (user_id) REFERENCES user(user_id),
        FOREIGN KEY (post_id) REFERENCES post(post_id),
        CHECK (post_id is not null or comment_id is not null) -- assures reaction is associated with a post or comment
    );  


    /* SKILL TABLE CONTAINING ALL USER SKILL ENTRIES AND THEIR RESPECTIVE DATA */
    CREATE TABLE skill (
        skill_id INT PRIMARY KEY AUTO_INCREMENT,
        user_id INT NOT NULL,
        skill VARCHAR(150) NOT NULL, -- all entries must contain a skill
        creation_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= creation_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY (user_id) REFERENCES user(user_id)
    );


    /* ORGANIZATION TABLE CONTAINING UNIQUE SCHOOL AND COMPANY ENTRIES AND THEIR RESPECTIVE DATA */
    CREATE TABLE organization (
        organization_id INT AUTO_INCREMENT PRIMARY KEY,
        type ENUM('school', 'company') NOT NULL,
        name VARCHAR(150) NOT NULL,
        image_url VARCHAR(255) NOT NULL CHECK(image_url LIKE 'https://invitro.ca/api/organization/%'), -- unique filepath for organization logo, auto populated based on verified organization via third party service provider
        phone VARCHAR(30) NOT NULL, -- auto populated based on verified organization via third party service provider
        street_number INT NOT NULL, -- auto populated based on verified organization via third party service provider
        street_name VARCHAR(150) NOT NULL, -- auto populated based on verified organization via third party service provider
        city VARCHAR(150) NOT NULL, -- auto populated based on verified organization via third party service provider
        postal_code VARCHAR(10) NOT NULL, -- auto populated based on verified organization via third party service provider
        province VARCHAR(3) NOT NULL, -- auto populated based on verified organization via third party service provider, stores up to 3 character abbrevation
        country VARCHAR(3) NOT NULL, -- auto populated based on verified organization via third party service provider, stores up to 3 character abbrevation
        creation_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= creation_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0 -- deleted flag defaults to 0
    );

     
    /* EXPERIENCE TABLE CONTAINING USER WORK EXPERIENCES AND RESPECTIVE DATA */
    CREATE TABLE experience (
        experience_id INT AUTO_INCREMENT PRIMARY KEY,
        user_id INT NOT NULL,
        job_title VARCHAR(150) NOT NULL,
        job_description TEXT,
        organization_id INT NOT NULL,
        start_date DATE NOT NULL CHECK(start_date <= getdate()), -- required field, start date must be entered and no future start dates can be selected
        end_date DATE CHECK(end_date >= start_date), -- nullable to allow work experiences that are currently active
        creation_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= reaction_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY (user_id) REFERENCES user(user_id),
        FOREIGN KEY (organization_id) REFERENCES organization(organization_id)
    );


    /* EDUCATION TABLE CONTAINING USER EDUCATION DETAILS AND RESPECTIVE DATA */
    CREATE TABLE education (
        education_id INT AUTO_INCREMENT PRIMARY KEY,
        user_id INT NOT NULL,
        organization_id INT NOT NULL,
        program VARCHAR(150) NOT NULL, -- required field
        description TEXT, -- optional field
        gpa DECIMAL(2,1) CHECK(gpa between 0.0 and 4.0), -- optional field, one digit aloud after decimal point accepting values between 0.0 and 4.0
        start_date DATE NOT NULL CHECK(start_date <= getdate()), -- required field, start date must be entered and no future start dates can be selected
        end_date DATE NOT NULL CHECK(end_date >= start_date), -- required field with date check, allows future dates (expected dates of graduation similar to LinkedIn)
        creation_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  -- time stamp at point of data collection
        modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP CHECK(modified_date >= reaction_date), -- last modified date must be greater than or equal to creation date
        deleted BOOLEAN NOT NULL DEFAULT 0, -- deleted flag defaults to 0
        FOREIGN KEY (user_id) REFERENCES user(user_id),
        FOREIGN KEY (organization_id) REFERENCES organization(organization_id)
    );
    


